const transformarPHPaJS=e=>e.includes("<?php")&&e.includes("?>")?/\$\w+/.test(e)?e.replace(/<\?php|<\?|\?>/g,"").replace(/\$([a-zA-Z_]\w*)/g,"let $1").replace(/echo\s+(.+?);/g,(e,t)=>`console.log(${t.replace(/\$([a-zA-Z_]\w*)/g,"${$1}")});`).replace(/console\.log\(\s*let\s+/g,"console.log(").replace(/return\s+let\s+/g,"return ").replace(/return\s+(\w+)\s+\+\s+let\s+(\w+)/g,"return $1 + $2").trim()||"No se detectaron elementos transformables.":"Error: No se encontraron variables PHP para transformar.":"Error: Código no válido, asegúrate de incluir '<?php' y '?>'.",obtenerElemento=e=>document.getElementById(e),manejarClick=(e,t)=>obtenerElemento(e).addEventListener("click",t);manejarClick("transformar",()=>{const e=obtenerElemento("inputPHP").value.trim();obtenerElemento("outputJS").textContent=e?transformarPHPaJS(e):"Error: Ingresa código PHP."}),manejarClick("limpiar",()=>{["inputPHP","outputJS"].forEach(e=>obtenerElemento(e).value=obtenerElemento(e).textContent="")}),manejarClick("seleccionar",()=>{const e=obtenerElemento("outputJS");if(e.textContent.trim()){const t=document.createRange();t.selectNodeContents(e),window.getSelection().removeAllRanges(),window.getSelection().addRange(t)}else alert("No hay código para seleccionar.")});